import React, { useState } from 'react';
import './App.css';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [formType, setFormType] = useState('login'); // 'login' or 'signup'
    const [error, setError] = useState('');

    const validatePassword = (pwd) => {
        const regex = /^(?=.*\d)(?=.*[!@#$%^&*])(?=.*[a-z])(?=.*[A-Z]).{8,}$/;
        return regex.test(pwd);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        // Validate password if signing up
        if (formType === 'signup') {
            if (!validatePassword(password)) {
                setError('Password must contain at least one number, one symbol, one uppercase letter, and one lowercase letter.');
                return;
            }
            if (password !== confirmPassword) {
                setError('Passwords do not match.');
                return;
            }
        }
        
        // Clear error message
        setError('');
        
        // Add your form submission logic here
        console.log('Username:', username);
        console.log('Password:', password);
        
        // You can use axios or fetch here to submit the form data
        // Example: await axios.post('/api/submit', { username, password });
    };

    return (
        <div className="login-container">
            <div className="login-card">
                <h2>{formType === 'login' ? 'Sign In' : 'Sign Up'}</h2>
                <form onSubmit={handleSubmit} className="login-form">
                    <div className="input-group">
                        <label htmlFor="username">Username</label>
                        <div className="line-input">
                            <input
                                type="text"
                                id="username"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                required
                            />
                            <span className="line"></span>
                        </div>
                    </div>
                    <div className="input-group">
                        <label htmlFor="password">Password</label>
                        <div className="line-input">
                            <input
                                type="password"
                                id="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                            <span className="line"></span>
                        </div>
                    </div>
                    {formType === 'signup' && (
                        <div className="input-group">
                            <label htmlFor="confirm-password">Confirm Password</label>
                            <div className="line-input">
                                <input
                                    type="password"
                                    id="confirm-password"
                                    value={confirmPassword}
                                    onChange={(e) => setConfirmPassword(e.target.value)}
                                    required
                                />
                                <span className="line"></span>
                            </div>
                        </div>
                    )}
                    {error && <p className="error-message">{error}</p>}
                    <button type="submit" className="login-button">
                        {formType === 'login' ? 'Sign In' : 'Sign Up'}
                    </button>
                </form>
                <p>
                    {formType === 'login' ? (
                        <span>
                            Don't have an account? <a href="#" onClick={() => setFormType('signup')}>Sign Up</a>
                        </span>
                    ) : (
                        <span>
                            Already have an account? <a href="#" onClick={() => setFormType('login')}>Sign In</a>
                        </span>
                    )}
                </p>
            </div>
        </div>
    );
};

export default Login;
