import React, { useState } from 'react';
import axios from 'axios';
import './App.css'; // Assuming you will create this CSS file for styling
import Register from './Register'; // Import the Register component

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isRegistering, setIsRegistering] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent the default form submission

    try {
      const response = await axios.post('/api/login', { email, password });

      // Store the token in localStorage or handle login success
      localStorage.setItem('authToken', response.data.token);
      console.log(response.data.message);
    } catch (err) {
      // Handle error (e.g., display error message)
      setError(err.response.data.message);
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        {isRegistering ? (
          <>
            <h2>Register</h2>
            <Register />
            <p>Already have an account? <button onClick={() => setIsRegistering(false)}>Login</button></p>
          </>
        ) : (
          <>
            <h2>Login</h2>
            <form onSubmit={handleSubmit} className="login-form">
              <div className="input-group">
                <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
              </div>
              <div className="input-group">
                <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
              </div>
              <button type="submit" className="login-button">Login</button>
              {error && <p>{error}</p>}
            </form>
            <p>Don't have an account? <button onClick={() => setIsRegistering(true)}>Register</button></p>
          </>
        )}
      </div>
    </div>
  );
}

export default Login;
