import React, { useState } from 'react';
import './App.css';
import Notification from './Notification';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection

const Login = () => {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [formType, setFormType] = useState('login'); // 'login' or 'signup'
    const [showPassword, setShowPassword] = useState(false);
    const [showConfirmPassword, setShowConfirmPassword] = useState(false);
    const [notification, setNotification] = useState({ message: '', type: '', visible: false });
    const navigate = useNavigate(); // Initialize useNavigate

    const validatePassword = (pwd) => {
        const regex = /^(?=.*\d)(?=.*[!@#$%^&*])(?=.*[a-z])(?=.*[A-Z]).{8,}$/;
        return regex.test(pwd);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (formType === 'signup') {
            if (!validatePassword(password)) {
                setNotification({ message: 'Password must contain at least one number, one symbol, one uppercase letter, and one lowercase letter.', type: 'error', visible: true });
                return;
            }
            if (password !== confirmPassword) {
                setNotification({ message: 'Passwords do not match.', type: 'error', visible: true });
                return;
            }
        }

        // Clear previous notifications
        setNotification({ message: '', type: 'loading', visible: true });

        try {
            const response = await fetch(`http://localhost:5000/api/${formType}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, email, password }),
            });

            const data = await response.json();

            if (response.ok) {
                if (formType === 'signup') {
                    setNotification({ message: 'Registration successful! Redirecting to login...', type: 'success', visible: true });
                    setTimeout(() => {
                        setNotification({ ...notification, visible: false });
                        navigate('/login'); // Redirect to login page after 2 seconds
                    }, 2000);
                } else {
                    setNotification({ message: 'Login successful!', type: 'success', visible: true });
                }
            } else {
                setNotification({ message: data.message, type: 'error', visible: true });
            }
        } catch (error) {
            setNotification({ message: 'Server error', type: 'error', visible: true });
        } finally {
            setNotification({ ...notification, visible: false });
        }
    };

    return (
        <div className="login-container">
            <div className="login-card">
                <h2>{formType === 'login' ? 'Sign In' : 'Sign Up'}</h2>
                <form onSubmit={handleSubmit} className="login-form">
                    {formType === 'signup' && (
                        <div className="input-group">
                            <label htmlFor="username">Username</label>
                            <div className="line-input">
                                <input
                                    type="text"
                                    id="username"
                                    value={username}
                                    onChange={(e) => setUsername(e.target.value)}
                                    required
                                />
                                <span className="line"></span>
                            </div>
                        </div>
                    )}
                    <div className="input-group">
                        <label htmlFor="email">Email</label>
                        <div className="line-input">
                            <input
                                type="email"
                                id="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                            <span className="line"></span>
                        </div>
                    </div>
                    <div className="input-group">
                        <label htmlFor="password">Password</label>
                        <div className="line-input">
                            <input
                                type={showPassword ? 'text' : 'password'}
                                id="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                            <i
                                className={`fas ${showPassword ? 'fa-eye-slash' : 'fa-eye'} password-toggle`}
                                onClick={() => setShowPassword(!showPassword)}
                            ></i>
                            <span className="line"></span>
                        </div>
                    </div>
                    {formType === 'signup' && (
                        <div className="input-group">
                            <label htmlFor="confirm-password">Confirm Password</label>
                            <div className="line-input">
                                <input
                                    type={showConfirmPassword ? 'text' : 'password'}
                                    id="confirm-password"
                                    value={confirmPassword}
                                    onChange={(e) => setConfirmPassword(e.target.value)}
                                    required
                                />
                                <i
                                    className={`fas ${showConfirmPassword ? 'fa-eye-slash' : 'fa-eye'} password-toggle`}
                                    onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                                ></i>
                                <span className="line"></span>
                            </div>
                        </div>
                    )}
                    {notification.visible && (
                        <Notification
                            message={notification.message}
                            type={notification.type}
                            visible={notification.visible}
                            onClose={() => setNotification({ ...notification, visible: false })}
                        />
                    )}
                    <button type="submit" className="login-button">
                        {formType === 'login' ? 'Sign In' : 'Sign Up'}
                    </button>
                </form>
                <p>
                    {formType === 'login' ? (
                        <span>
                            Don't have an account? <a href="#" onClick={() => setFormType('signup')}>Sign Up</a>
                        </span>
                    ) : (
                        <span>
                            Already have an account? <a href="#" onClick={() => setFormType('login')}>Sign In</a>
                        </span>
                    )}
                </p>
            </div>
        </div>
    );
};

export default Login;
